        LDA     ICSSC
        AND     #$1F
        STA     ICSSC
whileVal_Cpu0:                         ; Wait until the FLL switches to Low range DCO mode 
        LDA     ICSSC
        AND     #$C0
        BNE     whileVal_Cpu0

        ; SCGC1: TPM2=1,TPM1=1,ADC=1,IIC=1,SCI=1 
        LDA     #$FF
        STA     SCGC1                                               
        ; SCGC2: DBG=1,FLS=1,IRQ=1,KBI=1,ACMP=1,RTC=1,SPI=1 
        LDA     #$FF
        STA     SCGC2                                               
  ; Common initialization of the CPU registers 
